import React, { useState, useEffect } from 'react';

const CollectWaste = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [verificationImage, setVerificationImage] = useState(null);
  const [verificationFile, setVerificationFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [fetchingReports, setFetchingReports] = useState(true);
  
  useEffect(() => {
    fetchReports();
  }, []);
  
  const fetchReports = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/reports?status=pending', {
        headers: {
          'Authorization': token
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setReports(data.reports);
      }
    } catch (error) {
      console.error('Error fetching reports:', error);
    } finally {
      setFetchingReports(false);
    }
  };
  
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setVerificationFile(file);
      const reader = new FileReader();
      reader.onload = (event) => {
        setVerificationImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleCameraCapture = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'image/*';
    input.capture = 'environment';
    input.onchange = handleImageUpload;
    input.click();
  };
  
  const handleCollect = async () => {
    if (!selectedReport || !verificationFile) {
      alert('Please select a report and upload verification image');
      return;
    }
    
    setLoading(true);
    
    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();
      
      formData.append('verification_image', verificationFile);
      formData.append('report_id', selectedReport._id);
      
      const collectResponse = await fetch("http://localhost:5000/api/collect-waste", {
        method: 'POST',
        headers: {
          'Authorization': token
        },
        body: formData
      });
      
      if (collectResponse.ok) {
        const data = await collectResponse.json();
        alert(`Waste collected successfully! You earned ${data.points_earned} points.`);
        
        // Reset form
        setSelectedReport(null);
        setVerificationImage(null);
        setVerificationFile(null);
        
        // Refresh reports
        fetchReports();
      } else {
        const error = await collectResponse.json();
        alert(`Error: ${error.error}`);
      }
    } catch (error) {
      console.error('Error collecting waste:', error);
      alert('Error collecting waste. Please try again.');
    }
    
    setLoading(false);
  };

  const getWasteTypeIcon = (wasteType) => {
    const icons = {
      'vegetable': 'ü•¨',
      'fruitpeel': 'üçé',
      'garden': 'üåø',
      'paper': 'üìÑ',
      'cardboard': 'üì¶',
      'plastic': 'ü•§',
      'trash': 'üóëÔ∏è'
    };
    return icons[wasteType] || 'üóëÔ∏è';
  };
  
  if (fetchingReports) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading available reports...</div>
      </div>
    );
  }
  
  return (
    <div className="collect-waste-page">
      <h1>Collect Waste</h1>
      <p className="page-description">
        Browse available waste reports and earn points by collecting them responsibly.
      </p>
      
      <div className="card">
        <div className="card-title">Available Waste Reports ({reports.length})</div>
        
        {reports.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üìã</div>
            <h3>No waste reports available</h3>
            <p>All current reports have been collected or are in progress.</p>
            <button 
              className="btn btn-secondary"
              onClick={fetchReports}
            >
              Refresh Reports
            </button>
          </div>
        ) : (
          <div className="reports-grid">
            {reports.map(report => (
              <div 
                key={report._id} 
                className={`report-card ${selectedReport && selectedReport._id === report._id ? 'selected' : ''}`}
                onClick={() => setSelectedReport(report)}
              >
                <div className="report-image-container">
                  <img 
                    src={`data:image/jpeg;base64,${report.image}`} 
                    alt="Waste" 
                    className="report-image" 
                  />
                  <div className="waste-type-badge">
                    <span className="waste-icon">{getWasteTypeIcon(report.waste_type)}</span>
                    <span>{report.waste_type}</span>
                  </div>
                </div>
                
                <div className="report-details">
                  <div className="report-address">{report.address}</div>
                  <div className="report-meta">
                    <div className="report-amount">
                      <span className="meta-label">Amount:</span>
                      <span className="meta-value">{report.estimated_amount} kg</span>
                    </div>
                    <div className="report-confidence">
                      <span className="meta-label">Confidence:</span>
                      <span className="meta-value">{(report.confidence * 100).toFixed(1)}%</span>
                    </div>
                  </div>
                  <div className="report-date">
                    Reported: {new Date(report.created_at).toLocaleDateString()}
                  </div>
                </div>

                <div className="report-actions">
                  <button 
                    className="btn btn-sm btn-primary"
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedReport(report);
                    }}
                  >
                    {selectedReport && selectedReport._id === report._id ? 'Selected' : 'Select'}
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
      
      {selectedReport && (
        <div className="card verification-section">
          <div className="card-title">Verify Collection</div>
          <div className="selected-report-info">
            <h4>Selected Report:</h4>
            <p><strong>Type:</strong> {selectedReport.waste_type}</p>
            <p><strong>Location:</strong> {selectedReport.address}</p>
            <p><strong>Amount:</strong> {selectedReport.estimated_amount} kg</p>
          </div>
          
          <div className="form-group">
            <label className="form-label">Upload Verification Image</label>
            <div className="upload-section">
              <div 
                className="upload-container"
                onClick={() => document.getElementById('verification-upload').click()}
              >
                <div className="upload-icon">üì∑</div>
                <div className="upload-text">
                  {verificationImage ? 'Image selected - Click to change' : 'Click to upload verification image'}
                </div>
                <input
                  id="verification-upload"
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  style={{ display: 'none' }}
                />
              </div>
              <button 
                type="button"
                className="camera-btn"
                onClick={handleCameraCapture}
              >
                üì∏ Use Camera
              </button>
            </div>
            
            {verificationImage && (
              <div className="preview-container">
                <img src={verificationImage} alt="Verification preview" className="preview-image" />
                <button 
                  type="button"
                  className="remove-image"
                  onClick={() => {
                    setVerificationImage(null);
                    setVerificationFile(null);
                    document.getElementById('verification-upload').value = '';
                  }}
                >
                  Remove Image
                </button>
              </div>
            )}
          </div>
          
          <div className="verification-instructions">
            <h4>Verification Instructions:</h4>
            <ul>
              <li>Take a clear photo of the waste before collection</li>
              <li>Ensure the waste matches the reported type</li>
              <li>Include surrounding area for location verification</li>
              <li>Photo will be used to confirm successful collection</li>
            </ul>
          </div>
          
          <button 
            className="btn btn-primary"
            onClick={handleCollect}
            disabled={loading || !verificationFile}
          >
            {loading ? 'Verifying Collection...' : 'Verify and Collect Waste'}
          </button>
        </div>
      )}

      <div className="collection-tips">
        <h3>Collection Guidelines</h3>
        <div className="tips-grid">
          <div className="tip-card">
            <div className="tip-icon">üß§</div>
            <h4>Safety First</h4>
            <p>Always wear gloves and protective equipment when handling waste</p>
          </div>
          <div className="tip-card">
            <div className="tip-icon">üìç</div>
            <h4>Location Accuracy</h4>
            <p>Verify you're at the correct location before starting collection</p>
          </div>
          <div className="tip-card">
            <div className="tip-icon">‚ôªÔ∏è</div>
            <h4>Proper Disposal</h4>
            <p>Dispose of collected waste at appropriate recycling or waste facilities</p>
          </div>
          <div className="tip-card">
            <div className="tip-icon">üì∑</div>
            <h4>Document Everything</h4>
            <p>Take before and after photos for verification and points</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CollectWaste;
