import React, { useState } from 'react';

const WasteClassification = ({ user }) => {
  const [image, setImage] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [soilType, setSoilType] = useState('loamy');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  // Soil database
  const soilDb = {
    'clay': { name: 'Clay Soil' },
    'sandy': { name: 'Sandy Soil' },
    'loamy': { name: 'Loamy Soil' },
    'silty': { name: 'Silty Soil' }
  };

  // Nutrient database
  const nutrientDb = {
    'vegetable': {
      'Nitrogen (N)': '2.5-4% of dry weight',
      'Phosphorus (P)': '0.3-0.8% of dry weight',
      'Potassium (K)': '3-6% of dry weight',
      'Carbon:Nitrogen (C:N)': '15:1 (Ideal for composting)'
    },
    'fruitpeel': {
      'Nitrogen (N)': '1.5-3% of dry weight',
      'Potassium (K)': '8-12% of dry weight',
      'Calcium (Ca)': '0.5-2% of dry weight'
    },
    'garden': {
      'Nitrogen (N)': '1.5-3% of dry weight',
      'Phosphorus (P)': '0.2-0.5% of dry weight',
      'Silica (Si)': '2-5% (Strengthens plant cells)'
    },
    'paper': {
      'Carbon:Nitrogen (C:N)': '200:1 (High carbon)',
      'Lignin Content': '20-30% (Slow to decompose)'
    },
    'cardboard': {
      'Carbon:Nitrogen (C:N)': '350:1 (Very high carbon)',
      'Lignin Content': '25-35%'
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);
      setResult(null);
    }
  };

  const handleCameraCapture = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'image/*';
    input.capture = 'environment';
    input.onchange = handleImageUpload;
    input.click();
  };

  const classifyWaste = async () => {
    if (!imageFile) {
      alert('Please upload an image first');
      return;
    }

    setLoading(true);
    
    try {
      const formData = new FormData();
      formData.append('file', imageFile);
      formData.append('soil_type', soilType);
      formData.append('analysis_type', 'waste');

      const classifyResponse = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData
      });

      if (classifyResponse.ok) {
        await classifyWasteAPI();
      } else {
        simulateClassification();
      }
    } catch (error) {
      console.error('Classification error:', error);
      simulateClassification();
    }
    
    setLoading(false);
  };

  const classifyWasteAPI = async () => {
    try {
      const formData = new FormData();
      formData.append('file', imageFile);
      
      const response = await fetch('http://localhost:5000/api/classify-simple', {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        const data = await response.json();
        
        const wasteType = data.predicted_class;
        const confidence = data.confidence;
        
        const isBiodegradable = !['plastic', 'trash'].includes(wasteType);
        const nutrients = nutrientDb[wasteType] || {};
        
        setResult({
          class: wasteType,
          confidence: (confidence * 100).toFixed(2),
          biodegradable: isBiodegradable,
          message: isBiodegradable ? 
            'Biodegradable! Good for composting.' : 
            'Non-biodegradable! Dispose properly.',
          nutrients: nutrients,
          soilType: soilType
        });
      }
    } catch (error) {
      console.error('API Classification error:', error);
      simulateClassification();
    }
  };

  const simulateClassification = () => {
    const wasteTypes = ['vegetable', 'fruitpeel', 'garden', 'paper', 'cardboard', 'plastic', 'trash'];
    const randomType = wasteTypes[Math.floor(Math.random() * wasteTypes.length)];
    const confidence = (Math.random() * 30 + 70).toFixed(2);
    
    const isBiodegradable = !['plastic', 'trash'].includes(randomType);
    const nutrients = nutrientDb[randomType] || {};
    
    setResult({
      class: randomType,
      confidence: confidence,
      biodegradable: isBiodegradable,
      message: isBiodegradable ? 
        'Biodegradable! Good for composting.' : 
        'Non-biodegradable! Dispose properly.',
      nutrients: nutrients,
      soilType: soilType
    });
  };

  const getSoilRecommendations = (wasteType, soilType) => {
    const soilDeficiencies = {
      'clay': ['Nitrogen (N)', 'Phosphorus (P)', 'Organic Matter'],
      'sandy': ['Potassium (K)', 'Magnesium (Mg)', 'Water Retention'],
      'loamy': ['Calcium (Ca)', 'Sulfur (S)'],
      'silty': ['Zinc (Zn)', 'Manganese (Mn)']
    };

    const wasteNutrients = nutrientDb[wasteType] || {};
    const soilNeeds = soilDeficiencies[soilType] || [];
    
    const recommendations = [];
    Object.keys(wasteNutrients).forEach(nutrient => {
      const isNeeded = soilNeeds.some(need => nutrient.startsWith(need));
      if (isNeeded) {
        recommendations.push(`${nutrient}: ${wasteNutrients[nutrient]}`);
      }
    });
    
    return recommendations.length > 0 ? recommendations : ["No significant nutrient match for this soil type"];
  };

  const getWasteIcon = (wasteType) => {
    const icons = {
      'vegetable': 'ü•¨',
      'fruitpeel': 'üçé',
      'garden': 'üåø',
      'paper': 'üìÑ',
      'cardboard': 'üì¶',
      'plastic': 'ü•§',
      'trash': 'üóëÔ∏è'
    };
    return icons[wasteType] || 'üóëÔ∏è';
  };

  return (
    <div className="waste-classification-page">
      <h1>Waste Classification & Analysis</h1>
      <p className="page-description">
        Upload an image of waste to get AI-powered classification and soil recommendations.
      </p>

      {/* User Info */}
      <div className="user-points-banner">
        <span>Welcome, {user.name || user.email}!</span>
        <span className="points">Points: {user.points || 0}</span>
      </div>

      {/* Image Upload Section */}
      <div className="card">
        <div className="card-title">Upload Waste Image</div>
        
        <div className="upload-section">
          <div 
            className="upload-container"
            onClick={() => document.getElementById('image-upload').click()}
          >
            <div className="upload-icon">üì∑</div>
            <div className="upload-text">
              {image ? 'Image selected - Click to change' : 'Click to upload waste image'}
            </div>
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: 'none' }}
            />
          </div>
          <button 
            type="button"
            className="camera-btn"
            onClick={handleCameraCapture}
          >
            üì∏ Use Camera
          </button>
        </div>
        
        {image && (
          <div className="preview-container">
            <img 
              src={image} 
              alt="Uploaded waste" 
              className="preview-image"
            />
            <button 
              type="button"
              className="remove-image"
              onClick={() => {
                setImage(null);
                setImageFile(null);
                setResult(null);
                document.getElementById('image-upload').value = '';
              }}
            >
              Remove Image
            </button>
          </div>
        )}
      </div>

      {/* Soil Type Selection */}
      <div className="card">
        <div className="card-title">Select Your Soil Type</div>
        <select 
          value={soilType} 
          onChange={(e) => setSoilType(e.target.value)}
          className="form-select"
        >
          {Object.entries(soilDb).map(([key, value]) => (
            <option key={key} value={key}>{value.name}</option>
          ))}
        </select>
      </div>

      {/* Classify Button */}
      <button 
        onClick={classifyWaste}
        disabled={!image || loading}
        className={`btn ${image && !loading ? 'btn-primary' : 'btn-disabled'}`}
      >
        {loading ? 'Analyzing...' : 'Classify Waste'}
      </button>

      {/* Results Section */}
      {result && (
        <div className={`results-card ${result.biodegradable ? 'biodegradable' : 'non-biodegradable'}`}>
          <div className="result-header">
            <h2>Classification Results</h2>
            <div className="result-icon">
              {getWasteIcon(result.class)}
            </div>
          </div>
          
          <div className="result-details">
            <div className="result-item">
              <span className="result-label">Waste Type:</span>
              <span className="result-value">{result.class.toUpperCase()}</span>
            </div>
            <div className="result-item">
              <span className="result-label">Confidence:</span>
              <span className="result-value">{result.confidence}%</span>
            </div>
            <div className="result-item">
              <span className="result-label">Status:</span>
              <span className={`result-value ${result.biodegradable ? 'biodegradable' : 'non-biodegradable'}`}>
                {result.biodegradable ? '‚úÖ Biodegradable' : 'üö´ Non-biodegradable'}
              </span>
            </div>
          </div>

          <div className="result-message">
            <p>{result.message}</p>
          </div>

          {/* Nutrient Content */}
          {Object.keys(result.nutrients).length > 0 && (
            <div className="nutrients-section">
              <h3>Nutrient Content</h3>
              <div className="nutrients-grid">
                {Object.entries(result.nutrients).map(([nutrient, value]) => (
                  <div key={nutrient} className="nutrient-item">
                    <strong>{nutrient}:</strong> {value}
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Soil Recommendations */}
          {result.biodegradable && (
            <div className="recommendations-section">
              <h3>Recommendations for {soilDb[result.soilType].name}</h3>
              <div className="recommendations-list">
                {getSoilRecommendations(result.class, result.soilType).map((rec, index) => (
                  <div key={index} className="recommendation-item">
                    {rec}
                  </div>
                ))}
              </div>
              
              <div className="composting-tips">
                <h4>Composting Tips</h4>
                <ul>
                  <li>Mix with brown materials (dry leaves, paper) for better decomposition</li>
                  <li>Maintain proper moisture levels</li>
                  <li>Turn compost regularly for aeration</li>
                  <li>Ideal temperature: 140-160¬∞F (60-71¬∞C)</li>
                </ul>
              </div>
            </div>
          )}

          {/* Non-biodegradable disposal info */}
          {!result.biodegradable && (
            <div className="disposal-section">
              <h3>Proper Disposal</h3>
              <div className="disposal-tips">
                {result.class === 'plastic' && (
                  <ul>
                    <li>Check recycling number on the plastic</li>
                    <li>Clean before recycling</li>
                    <li>Take to designated recycling centers</li>
                  </ul>
                )}
                {result.class === 'trash' && (
                  <ul>
                    <li>Dispose in general waste bin</li>
                    <li>Consider if any parts can be recycled separately</li>
                    <li>Reduce usage of such items in future</li>
                  </ul>
                )}
              </div>
            </div>
          )}

          <button 
            className="btn btn-secondary"
            onClick={() => {
              setResult(null);
              setImage(null);
              setImageFile(null);
              document.getElementById('image-upload').value = '';
            }}
          >
            Classify Another Item
          </button>
        </div>
      )}

      {/* Educational Section */}
      {!result && (
        <div className="educational-section">
          <h2>How It Works</h2>
          <div className="how-it-works-grid">
            <div className="step-card">
              <div className="step-number">1</div>
              <h3>Upload Image</h3>
              <p>Take or upload a clear photo of the waste item</p>
            </div>
            <div className="step-card">
              <div className="step-number">2</div>
              <h3>AI Analysis</h3>
              <p>Our AI model classifies the waste type and determines biodegradability</p>
            </div>
            <div className="step-card">
              <div className="step-number">3</div>
              <h3>Get Recommendations</h3>
              <p>Receive personalized composting or disposal recommendations</p>
            </div>
          </div>

          <div className="tips-section">
            <h3>Classification Tips</h3>
            <div className="tips-grid">
              <div className="tip-card">
                <div className="tip-icon">üì∑</div>
                <h4>Good Photos</h4>
                <p>Take clear, well-lit photos with the waste item filling most of the frame</p>
              </div>
              <div className="tip-card">
                <div className="tip-icon">üéØ</div>
                <h4>Single Items</h4>
                <p>Focus on one type of waste at a time for best accuracy</p>
              </div>
              <div className="tip-card">
                <div className="tip-icon">üåç</div>
                <h4>Clean Items</h4>
                <p>Clean items are easier to classify and provide better results</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WasteClassification;
