import React, { useState, useEffect } from 'react';

const Home = ({ user }) => {
  const [stats, setStats] = useState({
    reports: 0,
    collections: 0,
    points: user?.points || 0,  // Add optional chaining
    rank: 0
  });
  
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);
  
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch("http://localhost:5000/api/reports?status=all&limit=5", {
          headers: {
            'Authorization': token
          }
        });
        
        if (response.ok) {
          const data = await response.json();
          setRecentActivity(data.reports);
          setStats(prev => ({
            ...prev,
            reports: data.reports.filter(r => r.user_id === user._id).length,
            collections: data.reports.filter(r => r.collected_by === user._id).length
          }));
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };
    
    fetchData();
  }, [user]);
  
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">🔄 Loading...</div>
      </div>
    );
  }
  
  return (
    <div className="home-page">
      {/* Branding Section */}
      <div className="branding-section">
        <div className="brand-logo">🌍</div>
        <h1 className="branding-title">Zero-to-Hero Waste Management</h1>
        <p className="branding-subtitle">
          Join our community in making waste management more efficient and rewarding!
        </p>
      </div>

      {/* Welcome Message */}
      <h2 className="welcome-msg">Welcome back, {user.name || user.email}!</h2>
      
      {/* Stats Section */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">📝</div>
          <div className="stat-label">Reports Made</div>
          <div className="stat-value">{stats.reports}</div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">🗑️</div>
          <div className="stat-label">Collections Done</div>
          <div className="stat-value">{stats.collections}</div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">🏆</div>
          <div className="stat-label">Points Earned</div>
          <div className="stat-value">{stats.points}</div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">📊</div>
          <div className="stat-label">Your Rank</div>
          <div className="stat-value">#{stats.rank || 'N/A'}</div>
        </div>
      </div>
      
      {/* Quick Actions */}
      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <div className="action-buttons">
          <button 
            className="action-btn report-btn"
            onClick={() => window.location.href = '/report-waste'}
          >
            <span className="action-icon">📝</span>
            Report Waste
          </button>
          <button 
            className="action-btn collect-btn"
            onClick={() => window.location.href = '/collect-waste'}
          >
            <span className="action-icon">🗑️</span>
            Collect Waste
          </button>
          <button 
            className="action-btn analysis-btn"
            onClick={() => window.location.href = '/soil-analysis'}
          >
            <span className="action-icon">🌱</span>
            Soil Analysis
          </button>
        </div>
      </div>
      
      {/* Recent Activity Section */}
      <div className="card">
        <div className="card-title">Recent Activity</div>
        
        {recentActivity.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">📋</div>
            <h3>No recent activity</h3>
            <p>Start by reporting waste or collecting existing reports!</p>
            <button 
              className="btn btn-primary"
              onClick={() => window.location.href = '/report-waste'}
            >
              Report Your First Waste
            </button>
          </div>
        ) : (
          <ul className="activity-list">
            {recentActivity.map(activity => (
              <li key={activity._id} className="activity-item">
                <div className="activity-icon">
                  {activity.status === 'completed' ? '✅' : '📝'}
                </div>
                <div className="activity-details">
                  <div className="activity-title">
                    {activity.waste_type} waste {activity.status === 'completed' ? 'collected' : 'reported'}
                  </div>
                  <div className="activity-address">{activity.address}</div>
                  <div className="activity-time">
                    {new Date(activity.created_at).toLocaleString()}
                  </div>
                </div>
                <div className="activity-meta">
                  <span className={`status-badge ${activity.status}`}>
                    {activity.status}
                  </span>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Achievement Section */}
      <div className="achievements-section">
        <h3>Your Achievements</h3>
        <div className="achievements-grid">
          <div className={`achievement ${stats.reports >= 1 ? 'unlocked' : 'locked'}`}>
            <div className="achievement-icon">🎯</div>
            <div className="achievement-title">First Report</div>
            <div className="achievement-desc">Report your first waste</div>
          </div>
          <div className={`achievement ${stats.collections >= 1 ? 'unlocked' : 'locked'}`}>
            <div className="achievement-icon">🏅</div>
            <div className="achievement-title">First Collection</div>
            <div className="achievement-desc">Collect your first waste</div>
          </div>
          <div className={`achievement ${stats.points >= 50 ? 'unlocked' : 'locked'}`}>
            <div className="achievement-icon">⭐</div>
            <div className="achievement-title">Point Master</div>
            <div className="achievement-desc">Earn 50+ points</div>
          </div>
          <div className={`achievement ${stats.reports >= 10 ? 'unlocked' : 'locked'}`}>
            <div className="achievement-icon">🌟</div>
            <div className="achievement-title">Reporter</div>
            <div className="achievement-desc">Make 10+ reports</div>
          </div>
        </div>
      </div>
    </div>
