import React, { useState } from "react";
import { Bell } from "lucide-react";

const ReportWaste = ({ user }) => {
  const [image, setImage] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [address, setAddress] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [estimatedAmount, setEstimatedAmount] = useState("");
  const [wasteType, setWasteType] = useState("");
  const [classificationResult, setClassificationResult] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [classifying, setClassifying] = useState(false);
  const [showVerificationDialog, setShowVerificationDialog] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [showNotifications, setShowNotifications] = useState(false);

  // Add notification function
  const addNotification = (title, message, pointsEarned) => {
    const newNotification = {
      id: Date.now(),
      title,
      message,
      time: new Date().toLocaleString(),
      isRead: false,
      pointsEarned,
    };
    setNotifications((prev) => [newNotification, ...prev]);
  };

  const unreadCount = notifications.filter((n) => !n.isRead).length;

  const markAsRead = (id) => {
    setNotifications((prev) =>
      prev.map((notif) =>
        notif.id === id ? { ...notif, isRead: true } : notif
      )
    );
  };

  const markAllAsRead = () => {
    setNotifications((prev) => prev.map((notif) => ({ ...notif, isRead: true })));
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);

      // Automatically classify the waste
      await classifyWasteImage(file);
    }
  };

  const classifyWasteImage = async (file) => {
    setClassifying(true);
    try {
      // Simulate classification API call
      await new Promise((resolve) => setTimeout(resolve, 2000));

      const mockResult = {
        predicted_class: "fruitpeel",
        confidence: 0.95,
      };

      setClassificationResult(mockResult);
      setWasteType(mockResult.predicted_class); // auto-fill waste type
    } catch (error) {
      console.error("Error classifying waste:", error);
      alert("Error classifying waste. Please try again.");
    }
    setClassifying(false);
  };

  const fetchSuggestions = async (query) => {
    if (query.length < 3) {
      setSuggestions([]);
      return;
    }
    // Mock suggestions
    setSuggestions([
      {
        name: "Main Street",
        street: "Main Street",
        city: "Agra",
        state: "UP",
        country: "India",
        postcode: "282001",
      },
      {
        name: "Bus Stop",
        street: "Civil Lines",
        city: "Agra",
        state: "UP",
        country: "India",
        postcode: "282002",
      },
    ]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!imageFile || !address || !estimatedAmount || !wasteType) {
      alert("Please fill all fields and upload an image");
      return;
    }

    setLoading(true);

    try {
      // Simulate API call
      await new Promise((resolve) => setTimeout(resolve, 1500));

      const mockResult = {
        points_earned: 5,
        address: address,
        waste_type: wasteType,
      };

      setResult(mockResult);

      // Add notification
      addNotification(
        "Waste Reported Successfully",
        `You've reported ${getWasteDescription(
          wasteType
        ).toLowerCase()} and earned ${mockResult.points_earned} points!`,
        mockResult.points_earned
      );

      // Show success popup
      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 3000);

      // Reset form
      setImage(null);
      setImageFile(null);
      setAddress("");
      setEstimatedAmount("");
      setWasteType("");
      setClassificationResult(null);
      setSuggestions([]);
      if (document.getElementById("image-upload")) {
        document.getElementById("image-upload").value = "";
      }
    } catch (error) {
      console.error("Error reporting waste:", error);
      alert("Error reporting waste. Please try again.");
    }

    setLoading(false);
  };

  const getWasteDescription = (type) => {
    const descriptions = {
      plastic: "Plastic waste",
      trash: "Mixed waste",
      paper: "Paper waste",
      cardboard: "Cardboard waste",
      vegetable: "Organic vegetable waste",
      fruitpeel: "Fruit peel waste",
      garden: "Garden waste",
    };
    return descriptions[type] || "Waste material";
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      {/* Popup */}
      {showPopup && result && (
        <div
          style={{
            position: "fixed",
            top: "20px",
            left: "50%",
            transform: "translateX(-50%)",
            backgroundColor: "#dcfce7",
            border: "1px solid #bbf7d0",
            borderRadius: "8px",
            padding: "14px 24px",
            display: "flex",
            alignItems: "center",
            color: "#065f46",
            fontSize: "15px",
            fontWeight: "500",
            zIndex: 3000,
          }}
        >
          âœ“ Report Submitted Successfully! You earned{" "}
          {result.points_earned || 5} points.
        </div>
      )}

      {/* Waste Form */}
      <form onSubmit={handleSubmit}>
        <h2>Report Waste</h2>

        {/* Image Upload */}
        <input
          id="image-upload"
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
        />
        {image && (
          <div style={{ marginTop: "10px", position: "relative" }}>
            <img
              src={image}
              alt="Preview"
              style={{ width: "200px", borderRadius: "8px" }}
            />
            {classifying && (
              <div
                style={{
                  position: "absolute",
                  top: "50%",
                  left: "50%",
                  transform: "translate(-50%, -50%)",
                  background: "rgba(0,0,0,0.6)",
                  color: "white",
                  padding: "10px 20px",
                  borderRadius: "6px",
                }}
              >
                Classifying...
              </div>
            )}
          </div>
        )}

        {/* Waste Type - Auto Filled */}
        <div style={{ marginTop: "15px" }}>
          <label>Waste Type</label>
          <input
            type="text"
            value={wasteType}
            readOnly
            style={{ marginLeft: "10px" }}
          />
        </div>

        {/* Address */}
        <div style={{ marginTop: "15px" }}>
          <label>Address</label>
          <input
            type="text"
            value={address}
            onChange={(e) => {
              setAddress(e.target.value);
              fetchSuggestions(e.target.value);
            }}
          />
        </div>

        {/* Estimated Amount */}
        <div style={{ marginTop: "15px" }}>
          <label>Estimated Amount</label>
          <input
            type="text"
            value={estimatedAmount}
            onChange={(e) => setEstimatedAmount(e.target.value)}
          />
        </div>

        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Report"}
        </button>
      </form>
    </div>
  );
};

export default ReportWaste;
