import React, { useState } from 'react';
import Notifications from './Notifications';

const Header = ({ onMenuClick, notifications, user }) => {
  const [showNotifications, setShowNotifications] = useState(false);

  return (
    <div className="header flex justify-between items-center p-4 shadow-md bg-white">
      {/* Left side: menu + title */}
      <div className="header-left flex items-center gap-2">
        <button className="menu-btn text-xl" onClick={onMenuClick}>â˜°</button>
        <h1 className="text-xl font-bold">Zero2Hero</h1>
      </div>

      {/* Right side: notifications + user info */}
      <div className="header-right flex items-center gap-4">
        {/* Notifications */}
        <div className="notification-container relative">
          <button
            className="notification-btn relative"
            onClick={() => setShowNotifications(!showNotifications)}
          >
            ðŸ””
            {notifications.filter(n => !n.read).length > 0 && (
              <span className="notification-badge absolute -top-1 -right-1 bg-red-500 text-white text-xs px-1 rounded-full">
                {notifications.filter(n => !n.read).length}
              </span>
            )}
          </button>

          {showNotifications && (
            <Notifications
              notifications={notifications}
              onClose={() => setShowNotifications(false)}
            />
          )}
        </div>

        {/* User Info */}
        <div className="user-info flex items-center gap-2">
          <div className="user-avatar bg-green-500 text-white rounded-full w-8 h-8 flex items-center justify-center">
            {user.name ? user.name.charAt(0).toUpperCase() : user.email.charAt(0).toUpperCase()}
          </div>
          <div className="user-details text-sm">
            <div className="user-name font-semibold">{user.name || user.email}</div>
            <div className="user-points text-gray-500">{user.points || 0} points</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
