import React, { useState } from 'react';

const ReportWaste = ({ user }) => {
  const [image, setImage] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [address, setAddress] = useState('');
  const [estimatedAmount, setEstimatedAmount] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleCameraCapture = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'image/*';
    input.capture = 'environment';
    input.onchange = handleImageUpload;
    input.click();
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!imageFile || !address || !estimatedAmount) {
      alert('Please fill all fields and upload an image');
      return;
    }
    
    setLoading(true);
    
    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();
      
      formData.append('image', imageFile);
      formData.append('address', address);
      formData.append('estimated_amount', estimatedAmount);
      
      const reportResponse = await fetch("http://localhost:5000/api/report-waste", {
        method: 'POST',
        headers: {
          'Authorization': token
        },
        body: formData
      });
      
      if (reportResponse.ok) {
        const data = await reportResponse.json();
        setResult(data);
        alert('Waste reported successfully!');
        
        // Reset form
        setImage(null);
        setImageFile(null);
        setAddress('');
        setEstimatedAmount('');
        document.getElementById('image-upload').value = '';
      } else {
        const error = await reportResponse.json();
        alert(`Error: ${error.error}`);
      }
    } catch (error) {
      console.error('Error reporting waste:', error);
      alert('Error reporting waste. Please try again.');
    }
    
    setLoading(false);
  };
  
  return (
    <div className="report-waste-page">
      <h1>Report Waste</h1>
      <p className="page-description">
        Help keep your community clean by reporting waste that needs collection.
      </p>
      
      <div className="card">
        <div className="card-title">Report New Waste</div>
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Upload Waste Image *</label>
            <div className="upload-section">
              <div 
                className="upload-container"
                onClick={() => document.getElementById('image-upload').click()}
              >
                <div className="upload-icon">üì∑</div>
                <div className="upload-text">
                  {image ? 'Image selected - Click to change' : 'Click to upload image'}
                </div>
                <input
                  id="image-upload"
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  style={{ display: 'none' }}
                />
              </div>
              <button 
                type="button"
                className="camera-btn"
                onClick={handleCameraCapture}
              >
                üì∏ Use Camera
              </button>
            </div>
            
            {image && (
              <div className="preview-container">
                <img src={image} alt="Waste preview" className="preview-image" />
                <button 
                  type="button"
                  className="remove-image"
                  onClick={() => {
                    setImage(null);
                    setImageFile(null);
                    document.getElementById('image-upload').value = '';
                  }}
                >
                  Remove Image
                </button>
              </div>
            )}
          </div>
          
          <div className="form-group">
            <label className="form-label">Location Address *</label>
            <input
              type="text"
              className="form-input"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Enter the exact location where waste is found"
              required
            />
            <small className="form-hint">
              Be as specific as possible (e.g., "Near Bus Stop, Main Street, City")
            </small>
          </div>
          
          <div className="form-group">
            <label className="form-label">Estimated Amount (kg) *</label>
            <input
              type="number"
              className="form-input"
              value={estimatedAmount}
              onChange={(e) => setEstimatedAmount(e.target.value)}
              placeholder="Enter estimated weight in kg"
              min="0.1"
              step="0.1"
              required
            />
            <small className="form-hint">
              Provide your best estimate of the waste weight
            </small>
          </div>
          
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={loading || !imageFile}
          >
            {loading ? 'Processing...' : 'Report Waste'}
          </button>
        </form>
      </div>
      
      {result && (
        <div className="result-container">
          <div className="result-header">
            <h3>Report Submitted Successfully!</h3>
            <div className="success-icon">‚úÖ</div>
          </div>
          
          <div className="result-details">
            <div className="result-item">
              <div className="result-label">Report ID</div>
              <div className="result-value">{result.report_id}</div>
            </div>
            
            <div className="result-item">
              <div className="result-label">Waste Type</div>
              <div className="result-value">{result.waste_type}</div>
            </div>
            
            <div className="result-item">
              <div className="result-label">AI Confidence</div>
              <div className="result-value">{(result.confidence * 100).toFixed(2)}%</div>
            </div>
            
            <div className="result-item">
              <div className="result-label">Location</div>
              <div className="result-value">{result.address}</div>
            </div>
            
            <div className="result-item">
              <div className="result-label">Estimated Amount</div>
              <div className="result-value">{result.estimated_amount} kg</div>
            </div>
          </div>

          <div className="next-steps">
            <h4>What happens next?</h4>
            <ul>
