import React, { useState } from 'react';
import { Camera, Upload, Trash2, Recycle, Leaf, AlertCircle, CheckCircle, Sparkles, TestTube, Lightbulb } from 'lucide-react';

const WasteClassification = ({ user = { name: 'User', points: 150 } }) => {
  const [image, setImage] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [soilType, setSoilType] = useState('loamy');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showSoilAnalysis, setShowSoilAnalysis] = useState(false);

  // Soil database
  const soilDb = {
    'clay': { name: 'Clay Soil', icon: '🏺', color: 'bg-amber-100 border-amber-300' },
    'sandy': { name: 'Sandy Soil', icon: '🏖️', color: 'bg-yellow-100 border-yellow-300' },
    'loamy': { name: 'Loamy Soil', icon: '🌱', color: 'bg-green-100 border-green-300' },
    'silty': { name: 'Silty Soil', icon: '🪨', color: 'bg-gray-100 border-gray-300' }
  };

  // Nutrient database
  const nutrientDb = {
    'vegetable': {
      'Nitrogen (N)': '2.5-4% of dry weight',
      'Phosphorus (P)': '0.3-0.8% of dry weight',
      'Potassium (K)': '3-6% of dry weight',
      'Carbon:Nitrogen (C:N)': '15:1 (Ideal for composting)'
    },
    'fruitpeel': {
      'Nitrogen (N)': '1.5-3% of dry weight',
      'Potassium (K)': '8-12% of dry weight',
      'Calcium (Ca)': '0.5-2% of dry weight'
    },
    'garden': {
      'Nitrogen (N)': '1.5-3% of dry weight',
      'Phosphorus (P)': '0.2-0.5% of dry weight',
      'Silica (Si)': '2-5% (Strengthens plant cells)'
    },
    'paper': {
      'Carbon:Nitrogen (C:N)': '200:1 (High carbon)',
      'Lignin Content': '20-30% (Slow to decompose)'
    },
    'cardboard': {
      'Carbon:Nitrogen (C:N)': '350:1 (Very high carbon)',
      'Lignin Content': '25-35%'
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);
      setResult(null);
      setShowSoilAnalysis(false);
    }
  };

  const handleCameraCapture = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'image/*';
    input.capture = 'environment';
    input.onchange = handleImageUpload;
    input.click();
  };

  const classifyWaste = async () => {
    if (!imageFile) {
      alert('Please upload an image first');
      return;
    }

    setLoading(true);
    setShowSoilAnalysis(false);
    
    try {
      const formData = new FormData();
      formData.append('file', imageFile);
      formData.append('soil_type', soilType);
      formData.append('analysis_type', 'waste');

      // Simulate API call - replace with actual API
      await new Promise(resolve => setTimeout(resolve, 2000));
      simulateClassification();
    } catch (error) {
      console.error('Classification error:', error);
      simulateClassification();
    }
    
    setLoading(false);
  };

  const simulateClassification = () => {
    const wasteTypes = ['vegetable', 'fruitpeel', 'garden', 'paper', 'cardboard', 'plastic', 'trash'];
    const randomType = wasteTypes[Math.floor(Math.random() * wasteTypes.length)];
    const confidence = (Math.random() * 30 + 70).toFixed(2);
    
    const isBiodegradable = !['plastic', 'trash'].includes(randomType);
    const nutrients = nutrientDb[randomType] || {};
    
    setResult({
      class: randomType,
      confidence: confidence,
      biodegradable: isBiodegradable,
      message: isBiodegradable ? 
        'Biodegradable! Perfect for composting and soil enrichment.' : 
        'Non-biodegradable! Please dispose responsibly.',
      nutrients: nutrients,
      soilType: soilType
    });
  };

  const getSoilRecommendations = (wasteType, soilType) => {
    const soilDeficiencies = {
      'clay': ['Nitrogen (N)', 'Phosphorus (P)', 'Organic Matter'],
      'sandy': ['Potassium (K)', 'Magnesium (Mg)', 'Water Retention'],
      'loamy': ['Calcium (Ca)', 'Sulfur (S)'],
      'silty': ['Zinc (Zn)', 'Manganese (Mn)']
    };

    const wasteNutrients = nutrientDb[wasteType] || {};
    const soilNeeds = soilDeficiencies[soilType] || [];
    
    const recommendations = [];
    Object.keys(wasteNutrients).forEach(nutrient => {
      const isNeeded = soilNeeds.some(need => nutrient.startsWith(need));
      if (isNeeded) {
        recommendations.push(`${nutrient}: ${wasteNutrients[nutrient]}`);
      }
    });
    
    return recommendations.length > 0 ? recommendations : ["This waste provides general soil enrichment benefits"];
  };

  const getWasteIcon = (wasteType) => {
    const icons = {
      'vegetable': '🥬',
      'fruitpeel': '🍎',
      'garden': '🌿',
      'paper': '📄',
      'cardboard': '📦',
      'plastic': '🥤',
      'trash': '🗑️'
    };
    return icons[wasteType] || '🗑️';
  };

  const resetClassification = () => {
    setResult(null);
    setImage(null);
    setImageFile(null);
    setShowSoilAnalysis(false);
    const input = document.getElementById('image-upload');
    if (input) input.value = '';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-blue-50 to-purple-50 p-4">
      <div className="max-w-4xl mx-auto space-y-6">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold bg-gradient-to-r from-green-600 to-blue-600 bg-clip-text text-transparent mb-3">
            🌱 AI Waste Classification & Soil Analysis
          </h1>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Upload waste images for intelligent classification, biodegradability analysis, and personalized soil recommendations
          </p>
        </div>

        {/* User Info Banner */}
        <div className="bg-gradient-to-r from-purple-500 to-pink-500 text-white p-4 rounded-2xl shadow-lg">
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-white/20 rounded-full flex items-center justify-center">
                <Sparkles className="h-6 w-6" />
              </div>
              <span className="text-lg font-semibold">Welcome, {user.name}!</span>
            </div>
            <div className="flex items-center space-x-2 bg-white/20 px-4 py-2 rounded-full">
              <span className="font-bold">{user.points}</span>
              <span>Points</span>
            </div>
          </div>
        </div>

        {/* Image Upload Section */}
        <div className="bg-white rounded-3xl shadow-xl p-6 border border-gray-100">
          <div className="flex items-center space-x-3 mb-6">
            <Camera className="h-6 w-6 text-blue-600" />
            <h2 className="text-2xl font-bold text-gray-800">Upload Waste Image</h2>
          </div>
          
          <div className="space-y-4">
            <div 
              className="border-3 border-dashed border-blue-300 rounded-2xl p-8 text-center cursor-pointer hover:border-blue-500 hover:bg-blue-50 transition-all duration-300 transform hover:scale-[1.02]"
              onClick={() => document.getElementById('image-upload').click()}
            >
              <div className="flex flex-col items-center space-y-4">
                <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center">
                  <Upload className="h-8 w-8 text-blue-600" />
                </div>
                <div>
                  <p className="text-lg font-semibold text-gray-700">
                    {image ? 'Image selected - Click to change' : 'Click to upload waste image'}
                  </p>
                  <p className="text-sm text-gray-500 mt-1">Supports JPG, PNG, WebP formats</p>
                </div>
                <input
                  id="image-upload"
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="hidden"
                />
              </div>
            </div>
            
            <button 
              type="button"
              className="w-full bg-gradient-to-r from-green-500 to-blue-500 text-white py-3 px-6 rounded-xl font-semibold hover:from-green-600 hover:to-blue-600 transition-all duration-300 transform hover:scale-[1.02] flex items-center justify-center space-x-2"
              onClick={handleCameraCapture}
            >
              <Camera className="h-5 w-5" />
              <span>Use Camera</span>
            </button>
          </div>
          
          {image && (
            <div className="mt-6 animate-fadeIn">
              <div className="relative rounded-2xl overflow-hidden shadow-lg">
                <img 
                  src={image} 
                  alt="Uploaded waste" 
                  className="w-full h-64 object-cover"
                />
                <div className="absolute top-4 right-4">
                  <button 
                    type="button"
                    className="bg-red-500 text-white p-2 rounded-full hover:bg-red-600 transition-colors duration-200"
                    onClick={resetClassification}
                  >
                    <Trash2 className="h-4 w-4" />
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Soil Type Selection - Only show when image is uploaded */}
        {image && (
          <div className="bg-white rounded-3xl shadow-xl p-6 border border-gray-100 animate-slideIn">
            <div className="flex items-center space-x-3 mb-6">
              <TestTube className="h-6 w-6 text-green-600" />
              <h2 className="text-2xl font-bold text-gray-800">Select Your Soil Type</h2>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              {Object.entries(soilDb).map(([key, value]) => (
                <button
                  key={key}
                  onClick={() => setSoilType(key)}
                  className={`p-4 rounded-xl border-2 transition-all duration-300 transform hover:scale-105 ${
                    soilType === key 
                      ? `${value.color} border-current shadow-lg` 
                      : 'bg-gray-50 border-gray-200 hover:bg-gray-100'
                  }`}
                >
                  <div className="text-center">
                    <div className="text-3xl mb-2">{value.icon}</div>
                    <div className="font-semibold text-gray-800">{value.name}</div>
                  </div>
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Classify Button */}
        {image && (
          <button 
            onClick={classifyWaste}
            disabled={loading}
            className={`w-full py-4 px-8 rounded-2xl font-bold text-lg transition-all duration-300 transform ${
              loading
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 hover:scale-[1.02] shadow-xl'
            } text-white animate-slideIn`}
          >
            {loading ? (
              <div className="flex items-center justify-center space-x-2">
                <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white"></div>
                <span>Analyzing...</span>
              </div>
            ) : (
              <div className="flex items-center justify-center space-x-2">
                <Sparkles className="h-6 w-6" />
                <span>Classify Waste</span>
              </div>
            )}
          </button>
        )}

        {/* Results Section */}
        {result && (
          <div className="space-y-6 animate-fadeIn">
            {/* Classification Results */}
            <div className={`rounded-3xl shadow-2xl p-8 border-2 ${
              result.biodegradable 
                ? 'bg-gradient-to-br from-green-50 to-emerald-50 border-green-200' 
                : 'bg-gradient-to-br from-red-50 to-pink-50 border-red-200'
            }`}>
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-3xl font-bold text-gray-800">Classification Results</h2>
                <div className="text-6xl animate-bounce">
                  {getWasteIcon(result.class)}
                </div>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
                <div className="bg-white/70 backdrop-blur-sm rounded-2xl p-4 text-center">
                  <div className="text-sm text-gray-600 mb-1">Waste Type</div>
                  <div className="text-xl font-bold text-gray-800">{result.class.toUpperCase()}</div>
                </div>
                <div className="bg-white/70 backdrop-blur-sm rounded-2xl p-4 text-center">
                  <div className="text-sm text-gray-600 mb-1">Confidence</div>
                  <div className="text-xl font-bold text-blue-600">{result.confidence}%</div>
                </div>
                <div className="bg-white/70 backdrop-blur-sm rounded-2xl p-4 text-center">
                  <div className="text-sm text-gray-600 mb-1">Status</div>
                  <div className={`text-lg font-bold flex items-center justify-center space-x-2 ${
                    result.biodegradable ? 'text-green-600' : 'text-red-600'
                  }`}>
                    {result.biodegradable ? <CheckCircle className="h-5 w-5" /> : <AlertCircle className="h-5 w-5" />}
                    <span>{result.biodegradable ? 'Biodegradable' : 'Non-biodegradable'}</span>
                  </div>
                </div>
              </div>

              <div className={`text-center p-4 rounded-2xl font-semibold text-lg ${
                result.biodegradable 
                  ? 'bg-green-100 text-green-800' 
                  : 'bg-red-100 text-red-800'
              }`}>
                {result.message}
              </div>
            </div>

            {/* Soil Analysis - Only show for biodegradable waste */}
            {result.biodegradable && (
              <>
                <div className="bg-white rounded-3xl shadow-xl p-6 border border-gray-100">
                  <div className="flex items-center justify-between mb-6">
                    <div className="flex items-center space-x-3">
                      <Leaf className="h-6 w-6 text-green-600" />
                      <h2 className="text-2xl font-bold text-gray-800">Soil Analysis</h2>
                    </div>
                    <button
                      onClick={() => setShowSoilAnalysis(!showSoilAnalysis)}
                      className="bg-gradient-to-r from-green-500 to-blue-500 text-white px-6 py-2 rounded-xl font-semibold hover:from-green-600 hover:to-blue-600 transition-all duration-300 flex items-center space-x-2"
                    >
                      <TestTube className="h-5 w-5" />
                      <span>{showSoilAnalysis ? 'Hide Analysis' : 'Show Analysis'}</span>
                    </button>
                  </div>

                  {showSoilAnalysis && (
                    <div className="space-y-6 animate-slideDown">
                      {/* Nutrient Content */}
                      {Object.keys(result.nutrients).length > 0 && (
                        <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-2xl p-6">
                          <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center space-x-2">
                            <span>🧪</span>
                            <span>Nutrient Content</span>
                          </h3>
                          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            {Object.entries(result.nutrients).map(([nutrient, value]) => (
                              <div key={nutrient} className="bg-white/70 backdrop-blur-sm rounded-xl p-4 hover:shadow-md transition-all duration-300 transform hover:scale-[1.02]">
                                <div className="font-semibold text-gray-700">{nutrient}</div>
                                <div className="text-blue-600 font-medium">{value}</div>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {/* Soil Recommendations */}
                      <div className="bg-gradient-to-r from-green-50 to-yellow-50 rounded-2xl p-6">
                        <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center space-x-2">
                          <span>{soilDb[result.soilType].icon}</span>
                          <span>Recommendations for {soilDb[result.soilType].name}</span>
                        </h3>
                        <div className="space-y-3">
                          {getSoilRecommendations(result.class, result.soilType).map((rec, index) => (
                            <div key={index} className="bg-white/70 backdrop-blur-sm rounded-xl p-4 hover:shadow-md transition-all duration-300 transform hover:scale-[1.02] flex items-start space-x-3">
                              <Lightbulb className="h-5 w-5 text-yellow-500 mt-1 flex-shrink-0" />
                              <div className="text-gray-700">{rec}</div>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  )}
                </div>

                {/* Composting Tips - Enhanced with effects */}
                <div className="bg-gradient-to-r from-emerald-500 to-teal-500 text-white rounded-3xl shadow-2xl p-8">
                  <h3 className="text-2xl font-bold mb-6 flex items-center space-x-3">
                    <Recycle className="h-8 w-8" />
                    <span>🌟 Pro Composting Tips</span>
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {[
                      { icon: '🍂', tip: 'Mix with brown materials (dry leaves, paper) for better decomposition' },
                      { icon: '💧', tip: 'Maintain proper moisture levels - should feel like a wrung-out sponge' },
                      { icon: '🔄', tip: 'Turn compost regularly for proper aeration and faster breakdown' },
                      { icon: '🌡️', tip: 'Ideal temperature: 140-160°F (60-71°C) for optimal decomposition' }
                    ].map((item, index) => (
                      <div key={index} className="bg-white/20 backdrop-blur-sm rounded-2xl p-4 hover:bg-white/30 transition-all duration-300 transform hover:scale-[1.05] hover:shadow-lg">
                        <div className="flex items-start space-x-3">
                          <div className="text-2xl">{item.icon}</div>
                          <div className="text-white/90 font-medium">{item.tip}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </>
            )}

            {/* Non-biodegradable disposal info */}
            {!result.biodegradable && (
              <div className="bg-gradient-to-r from-orange-500 to-red-500 text-white rounded-3xl shadow-2xl p-8">
                <h3 className="text-2xl font-bold mb-6 flex items-center space-x-3">
                  <AlertCircle className="h-8 w-8" />
                  <span>Proper Disposal Guidelines</span>
                </h3>
                <div className="bg-white/20 backdrop-blur-sm rounded-2xl p-6">
                  {result.class === 'plastic' && (
                    <div className="space-y-3">
                      {[
                        '🔍 Check recycling number on the plastic container',
                        '🧽 Clean thoroughly before placing in recycling bin',
                        '🏢 Take to designated recycling centers if needed',
                        '🌱 Consider eco-friendly alternatives for future use'
                      ].map((tip, index) => (
                        <div key={index} className="flex items-center space-x-3 text-white/90">
                          <div className="w-2 h-2 bg-white rounded-full"></div>
                          <div>{tip}</div>
                        </div>
                      ))}
                    </div>
                  )}
                  {result.class === 'trash' && (
                    <div className="space-y-3">
                      {[
                        '🗑️ Dispose in general waste bin safely',
                        '♻️ Check if any parts can be recycled separately',
                        '📉 Reduce usage of such items in the future',
                        '🌍 Consider environmental impact of alternatives'
                      ].map((tip, index) => (
                        <div key={index} className="flex items-center space-x-3 text-white/90">
                          <div className="w-2 h-2 bg-white rounded-full"></div>
                          <div>{tip}</div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Action Button */}
            <button 
              className="w-full bg-gradient-to-r from-indigo-500 to-purple-500 text-white py-4 px-8 rounded-2xl font-bold text-lg hover:from-indigo-600 hover:to-purple-600 transition-all duration-300 transform hover:scale-[1.02] shadow-xl flex items-center justify-center space-x-2"
              onClick={resetClassification}
            >
              <Sparkles className="h-6 w-6" />
              <span>Classify Another Item</span>
            </button>
          </div>
        )}

        {/* Educational Section - Show when no results */}
        {!result && !image && (
          <div className="bg-white rounded-3xl shadow-xl p-8 border border-gray-100">
            <h2 className="text-3xl font-bold text-center text-gray-800 mb-8">How It Works</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              {[
                { step: '1', icon: <Camera className="h-12 w-12" />, title: 'Upload Image', desc: 'Take or upload a clear photo of the waste item', color: 'from-blue-400 to-blue-600' },
                { step: '2', icon: <Sparkles className="h-12 w-12" />, title: 'AI Analysis', desc: 'Our AI model classifies waste type and determines biodegradability', color: 'from-purple-400 to-purple-600' },
                { step: '3', icon: <Leaf className="h-12 w-12" />, title: 'Get Recommendations', desc: 'Receive personalized composting or disposal recommendations', color: 'from-green-400 to-green-600' }
              ].map((step, index) => (
                <div key={index} className="text-center group">
                  <div className={`w-20 h-20 bg-gradient-to-br ${step.color} rounded-full flex items-center justify-center text-white mx-auto mb-4 group-hover:scale-110 transition-transform duration-300 shadow-lg`}>
                    {step.icon}
                  </div>
                  <div className="text-2xl font-bold text-gray-800 mb-2">{step.title}</div>
                  <div className="text-gray-600 leading-relaxed">{step.desc}</div>
                </div>
              ))}
            </div>

            <div className="mt-12">
              <h3 className="text-2xl font-bold text-center text-gray-800 mb-8">💡 Pro Tips for Best Results</h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {[
                  { icon: '📷', title: 'Good Photos', desc: 'Take clear, well-lit photos with the waste item filling most of the frame', color: 'border-blue-200 bg-blue-50' },
                  { icon: '🎯', title: 'Single Items', desc: 'Focus on one type of waste at a time for best accuracy', color: 'border-purple-200 bg-purple-50' },
                  { icon: '🌍', title: 'Clean Items', desc: 'Clean items are easier to classify and provide better results', color: 'border-green-200 bg-green-50' }
                ].map((tip, index) => (
                  <div key={index} className={`p-6 rounded-2xl border-2 ${tip.color} hover:shadow-lg transition-all duration-300 transform hover:scale-[1.05]`}>
                    <div className="text-4xl mb-4 text-center">{tip.icon}</div>
                    <div className="text-lg font-bold text-gray-800 mb-2 text-center">{tip.title}</div>
                    <div className="text-gray-600 text-center leading-relaxed">{tip.desc}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        
        @keyframes slideIn {
          from { opacity: 0; transform: translateX(-20px); }
          to { opacity: 1; transform: translateX(0); }
        }
        
        @keyframes slideDown {
          from { opacity: 0; transform: translateY(-10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        
        .animate-fadeIn {
          animation: fadeIn 0.6s ease-out;
        }
        
        .animate-slideIn {
          animation: slideIn 0.6s ease-out;
        }
        
        .animate-slideDown {
          animation: slideDown 0.4s ease-out;
        }
      `}</style>
    </div>
  );
};

export default WasteClassification;
